INSERT INTO `channels` (`avatar`, `created_by`, `id`, `is_active`, `modified_by`, `created_date`, `modified_date`, `description`, `name`) VALUES (12, 1, 1, 1, NULL, '2024-05-24 09:56:19', NULL, 'TechTalk Lounge is the ultimate destination for tech enthusiasts to engage in lively discussions about the latest innovations, gadgets, and breakthroughs in the world of technology. Whether you\'re a seasoned developer, a curious consumer, or just someone fascinated by the rapid advancements in the tech realm, this channel provides a space to share insights, troubleshoot tech issues, and explore the future of digital evolution.', 'TechTalk Lounge');
INSERT INTO `channels` (`avatar`, `created_by`, `id`, `is_active`, `modified_by`, `created_date`, `modified_date`, `description`, `name`) VALUES (13, 1, 2, 1, NULL, '2024-05-24 09:57:09', NULL, 'Bookworm Haven is a virtual sanctuary for literary aficionados and bookworms alike. Here, the written word takes center stage as members dive into deep conversations about classic novels, contemporary bestsellers, and hidden literary gems. From genre-specific recommendations to thought-provoking discussions on character development and plot twists, this channel is a haven for those who find solace and joy in the pages of a good book.', 'Bookworm Haven');
INSERT INTO `channels` (`avatar`, `created_by`, `id`, `is_active`, `modified_by`, `created_date`, `modified_date`, `description`, `name`) VALUES (14, 1, 3, 1, NULL, '2024-05-24 09:57:46', NULL, 'Fitness Fanatics Hub is the go-to spot for health enthusiasts, gym buffs, and anyone committed to living an active lifestyle. Whether you\'re a seasoned athlete, a beginner on a fitness journey, or someone just looking for motivation, this channel provides a supportive community to share workout tips, nutrition advice, and celebrate personal milestones. Join the conversation and empower yourself on the path to a healthier, more active life.', 'Fitness Fanatics Hub');
INSERT INTO `channels` (`avatar`, `created_by`, `id`, `is_active`, `modified_by`, `created_date`, `modified_date`, `description`, `name`) VALUES (15, 1, 4, 1, NULL, '2024-05-24 10:02:32', NULL, 'Creative Minds Collective is a vibrant space where artists, writers, musicians, and creators of all kinds come together to inspire and be inspired. Share your latest artistic endeavors, seek feedback on your work, or collaborate with fellow creatives. This channel is a melting pot of imagination, fostering a community that encourages exploration, experimentation, and the celebration of diverse forms of self-expression.', 'Creative Minds Collective');
INSERT INTO `channels` (`avatar`, `created_by`, `id`, `is_active`, `modified_by`, `created_date`, `modified_date`, `description`, `name`) VALUES (16, 1, 5, 1, NULL, '2024-05-24 10:03:55', NULL, 'Green Living Oasis is a dedicated channel for eco-conscious individuals striving to make a positive impact on the environment. From sustainable living tips and eco-friendly product recommendations to discussions on climate change and conservation efforts, this channel serves as a gathering place for those passionate about fostering a greener, more sustainable future. Join us in the pursuit of eco-conscious living and share your insights on preserving our planet.', 'Green Living Oasis');
INSERT INTO `channels` (`avatar`, `created_by`, `id`, `is_active`, `modified_by`, `created_date`, `modified_date`, `description`, `name`) VALUES (17, 1, 6, 1, NULL, '2024-05-24 10:07:56', NULL, 'Global Gastronomy Galore is a delectable channel where foodies from around the world come together to share their love for culinary delights. From international recipes and cooking tips to discussions on food culture and unique dining experiences, this channel is a feast for the senses. Join fellow epicureans in exploring the rich tapestry of global cuisines and discover new flavors that will tantalize your taste buds.', 'Global Gastronomy Galore');
INSERT INTO `channels` (`avatar`, `created_by`, `id`, `is_active`, `modified_by`, `created_date`, `modified_date`, `description`, `name`) VALUES (18, 1, 7, 1, NULL, '2024-05-24 10:08:39', NULL, 'Adventure Seekers Network is the ultimate virtual basecamp for thrill-seekers and outdoor enthusiasts. Whether you\'re into hiking, rock climbing, backpacking, or extreme sports, this channel is your go-to for adrenaline-pumping discussions. Share your latest adventures, seek advice on gear, and connect with fellow adrenaline junkies who understand the call of the wild. Embark on a journey with like-minded individuals who live for the thrill of the great outdoors.', 'Adventure Seekers Network');
INSERT INTO `channels` (`avatar`, `created_by`, `id`, `is_active`, `modified_by`, `created_date`, `modified_date`, `description`, `name`) VALUES (19, 1, 8, 1, NULL, '2024-05-24 10:11:27', NULL, 'Language Exchange Plaza is a dynamic hub for language learners and polyglots to come together and embark on a linguistic journey. Whether you\'re brushing up on a second language or starting from scratch, this channel provides a supportive environment for practicing and learning new languages. Engage in language exchange sessions, share helpful resources, and connect with speakers of various languages around the globe. Immerse yourself in a diverse linguistic landscape and broaden your cultural horizons.', 'Language Exchange Plaza');
